import matplotlib.pyplot as plt

def plot_lines(list1, list2, save_path=None):
    x1, y1 = zip(*list1)
    x2, y2 = zip(*list2)

    plt.plot(x1, y1, label='Solid Line', linestyle='-', marker='o')
    plt.plot(x2, y2, label='Dotted Line', linestyle='--', marker='x')

    plt.xlabel('X-axis')
    plt.ylabel('Y-axis')
    plt.title('Two Lines Plot')
    plt.legend()
    plt.grid(True)

    if save_path:
        plt.savefig(save_path, format='png')
    else:
        plt.show()

